void setup_graphics2() {
  // copy background
  vram_adr(0x0);
  vram_write(TILESET2, sizeof(TILESET2));
  // copy sprites
  vram_adr(0x1000);
  vram_write(TILESET2, sizeof(TILESET2));
  // activate vram buffer
  vrambuf_clear();
  set_vram_update(updbuf);
}

#pragma rodata-name("CODE6")
// Main PRG bank that is always accessible.
void capture_controller_input(void) {
  // read the first controller
  pad1_new = pad_trigger(0);
  pad1 = pad_state(0);
  
  // when `A` pressed change tileset.
  if(pad1_new & PAD_A) {
    ++tileset_new;
    if (tileset_new == 4) {
      tileset_new = 1;
    }
  }
}

void setup_display(void) {
  // when tileset changed load new tileset.
  if (tileset != tileset_new) {
    tileset = tileset_new; 
    ppu_off();
    if (tileset == 1) {
      MMC3_PRG_A000(3);
      setup_graphics1();
    } else if (tileset == 2) {
      MMC3_PRG_A000(5);
      setup_graphics2();
    } else if (tileset == 3) {
      MMC3_PRG_8000(1);
      setup_graphic_a();
    }
    ppu_on_all();     
  }
}

// main function, run after console reset.
// Must be in accessable bank.
void main(void) {
  // set palette colors
  pal_bg(PALETTE);

  // write text to name table
  // set address
  vram_adr(NTADR_A(2,12));
  // write bytes to video RAM
  vram_write("PRESS 'A' TO CHANGE TILESET!", 28);

  // infinite loop
  while (1) {
    // wait for next frame
    ppu_wait_nmi();
    // get do controller logic
    capture_controller_input();
    // setup display
    setup_display();
  }
  
}
